import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent implements OnInit {
  public Eventos: any = [];
  public EventosFiltrados: any = [];

  widthImg = 50;
  marginImg = 2;
  showImg = true;
  private _filtrarLista: string = '';

  public get filtroLista() {
    return this._filtrarLista;
  }

  public set filtroLista(value: string) {
    this._filtrarLista = value;
    this.EventosFiltrados = this.filtroLista ? this.filterEvents(this.filtroLista) : this.Eventos;
  }

  filterEvents(filtrarPor: string): any{
    filtrarPor = filtrarPor.toLocaleLowerCase();
    return this.Eventos.filter((ev: any) => ev.tema.toLocaleLowerCase().indexOf(filtrarPor) !== -1 ||
                                            ev.local.toLocaleLowerCase().indexOf(filtrarPor) !== -1)
  }

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.GetEventos();
  }

  alterarImg() {
    this.showImg = !this.showImg;
  }
  public GetEventos(): void{

    this.http.get('https://localhost:5001/api/Eventos').subscribe(
      response => {
        this.Eventos = response;
        this.EventosFiltrados = this.Eventos;
      },
      error => console.log(error)
    );
}
}
<h2>Eventos</h2>

<hr>

<div class="row row-cols-lg-auto g-3 align-items-center" action="">
  <div>
    <label class="visually-hidden" for="inlineFormInputGroupUsername"></label>
    <div class="input-group">
      <label class="mr-2">Filtro</label>
      <input
      type="text"
       class="form-control"
        placeholder="buscar"
        [(ngModel)]="filtroLista"/>
    </div>
  </div>
  <!-- <div>
    <button type="submit" class="btn btn-outline-success">Buscar</button>
  </div> -->
</div>
<!-- <h3>Filter: {{filtroLista}}</h3> -->
<hr>

<table class="table">
  <thead class="table-dark">
    <tr>
      <th class="text-center" scope="col">
        <button type="button" class="btn btn-outline-light btn-sm"
        (click)="alterarImg()">{{!showImg ? 'Exibir Imagem': 'Ocultar Imagem'}}</button>
      </th>
      <th class="text-center" scope="col">#</th>
      <th class="text-center" scope="col">Tema</th>
      <th class="text-center" scope="col">Local</th>
      <th class="text-center" scope="col">Data</th>
      <th class="text-center" scope="col">Qtd Pessoas</th>
      <th class="text-center" scope="col">Lote</th>
      <button type="button" class="btn btn-primary btn-sm">Small button</button>
      <th class="text-center" scope="col">Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="Eventos && Eventos.length">
    <tr *ngFor="let evento of EventosFiltrados">
      <td>
        <img [collapse]="!showImg"
        [isAnimated]="true"
         src="/assets/{{evento.imagemURL}}"
        [style.width.px] = widthImg
        [style.margin.px] = marginImg
        >
      </td>
      <th class="text-center" scope="row">{{evento.eventoId}}</th>
      <td class="text-center">{{evento.tema}}</td>
      <td class="text-center">{{evento.local}}</td>
      <td class="text-center">{{evento.dataEvento}}</td>
      <td class="text-center">{{evento.qtdPessoas}}</td>
      <td class="text-center">{{evento.lote}}</td>
      <td class="text-center"></td>
    </tr>
  </tbody>
  <tfoot *ngIf="!Eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum Evento Encontrado</h4>
      </td>
    </tr>
  </tfoot>
</table>
